$(function(){$('.inview').each(function(index){$(this).addClass('animate__animated');$(this).attr('data-inview',index+1)});var scroll;var winH=$(window).height();$(window).on('scroll',function(){scroll=$(window).scrollTop();$('.inview').each(function(){var $this=$(this);var objH=$this.outerHeight();var objTop=$this.offset().top;var objBottom=objTop+objH;var offset=parseInt($this.data('inview-offset'))||0;var triggerTop=objTop-offset;var triggerBottom=objBottom+offset;if(scroll+winH>=triggerTop&&scroll<=triggerBottom){$this.addClass("inview-in");$this.removeClass("inview-out");var inviewClasses=$this.data('inview-in');if(inviewClasses){var classArray=inviewClasses.split(',');classArray.forEach(function(className){$this.addClass(className.trim())})}}else{$this.removeClass("inview-in");$this.addClass("inview-out");var inviewClasses=$this.data('inview-out');if(inviewClasses){var classArray=inviewClasses.split(',');classArray.forEach(function(className){$this.removeClass(className.trim())})}}})})})
